<template>
  <div class="base">
    <div class="title">
      <div class="left">
        <h2 style="margin-top: 20px;">教学课程</h2>
        <div class="teach-class">
          <el-button>新建课程</el-button>
          <el-button>新手指引</el-button>
        </div>
      </div>
      <div class="right">
        <img
          src="data:image/png;base64,"
          alt="">
      </div>
    </div>
    <div class="teach-class-list">
      <ul>
        <li v-for="item in displayedTasks" :key="item.courseId" @click="SelectClass(item)">
          <div class="header">
            <!-- 这里展示本课程的最新作业的情况，但是还没做 -->
            <p style="margin-top: 10px;">{{ item.courseName }}</p>
          </div>
          <div class="footer">
            <p><img src="@/assets/img/人数.png" alt="">7</p>
            <!-- <p><img src="@/assets/img/人数.png" alt="">{{ item.people }}</p> -->
          </div>
        </li>
      </ul>
    </div>
    <div class="pageing">
      <el-config-provider :locale="zhCn">
        <el-pagination :current-page="testPage.pageNum" background :page-size="testPage.pageSize" :page-sizes="[10]"
          v-if="testPage.taskList.length > 0" layout="total, prev, pager, next, jumper"
          :total="testPage.taskList.length" @size-change="handleSizeChange" @current-change="handleCurrentChange" />
      </el-config-provider>
    </div>
  </div>
</template>

<script setup lang="ts">
import { zhCn } from "element-plus/es/locale/index.mjs";
import { ref, reactive, computed, onMounted } from "vue";
import { useRouter } from "vue-router";
import { useUserStore } from "../../store/user";
import { getCourseListAPI } from "@/api/CourseAPI";

const userStore = useUserStore();

interface taskList {
  courseId: number,
  courseName: string,
  people: number,
}

const testPage = reactive({
  taskList: [] as taskList[],
  pageSize: 12,
  pageNum: 1
})

//列表展示
const handleSizeChange = (val: number) => {
  testPage.pageSize = val
}
const handleCurrentChange = (val: number) => {
  testPage.pageNum = val
  window.scrollTo(0, 0)
}
// 根据当前页码和每页显示数量计算当前显示的章节列表
const displayedTasks = computed(() => {
  const start = (testPage.pageNum - 1) * testPage.pageSize;
  const end = start + testPage.pageSize;
  return testPage.taskList.slice(start, end);
});

// 跳转课程情况
const router = useRouter();
const userInfo = userStore.userInfo;
// 从本地存储获取课程列表
const getTaskList = async () => {
  let data = {
    pageNum: 1,
    pageSize: 9999
  }
  const res = await getCourseListAPI(data);

  if(res.data.code === 200) {
    testPage.taskList = res.data.rows;
    // localStorage.setItem('taskList', JSON.stringify(res.data.rows));
  } else {
    console.log('获取课程列表失败');
  }
}

// 选择课程函数
const SelectClass = (item: object) => {
  // localStorage.setItem('SelectClassName', name);
  userStore.setSelectClass(item);
  // 跳转到首页页面
  router.push('/home/homepage');
}

onMounted(() => {
  getTaskList();
})
</script>

